const EnrollmentProfile = 'Employee';
import { nim } from './nim';

/**
   * Retrieves the current enrollment for user
   * @param {string} ExternalID - The external id for the enrollment
   * @returns Enrollments mapping external id
   */
export async function getEnrollment(ExternalID: string) {
  let results = await nim.filterExecute('App_PasswordResetEnrollment_GetEnrollment', { ExternalID: ExternalID} )

  return {
    ID: results?.[0]?.ID ?? '',
    Profile: results?.[0]?.Profile ?? '',
    SmsPhoneNumber1: results?.[0]?.SmsPhoneNumber1 ?? '',
    SmsPhoneNumber2: results?.[0]?.SmsPhoneNumber2 ?? '',
    SmsPhoneNumber3: results?.[0]?.SmsPhoneNumber3 ?? '',
    MailAddress1: results?.[0]?.MailAddress1 ?? '',
    MailAddress2: results?.[0]?.MailAddress2 ?? '',
    MailAddress3: results?.[0]?.MailAddress3 ?? ''
  }
}

export async function updateEnrollment(ID: number, Profile: string, ExternalID: string, SmsPhoneNumber1: string, SmsPhoneNumber2: string, SmsPhoneNumber3: string, MailAddress1: string, MailAddress2: string, MailAddress3: string) {
  if(ID > 0) {
    // Update Enrollment
    await nim.targetSystemFunctionRun('internal', 'password_reset_update', {
      ID: ID,
      SmsPhoneNumber1: SmsPhoneNumber1 ?? null,
      SmsPhoneNumber2: SmsPhoneNumber2 ?? null,
      SmsPhoneNumber3: SmsPhoneNumber3 ?? null,
      MailAddress1: MailAddress1 ?? null,
      MailAddress2: MailAddress2 ?? null,
      MailAddress3: MailAddress3 ?? null
    })
  } else {
    // Create Enrollment
    await nim.targetSystemFunctionRun('internal', 'password_reset_create', {
      ExternalID: ExternalID,
      Profile: EnrollmentProfile,
      SmsPhoneNumber1: SmsPhoneNumber1,
      SmsPhoneNumberLabel1: SmsPhoneNumber1 ? "Send SMS to" : '',
      SmsPhoneNumber2: SmsPhoneNumber2,
      SmsPhoneNumberLabel2: SmsPhoneNumber2 ? "Send SMS to" : '',
      SmsPhoneNumber3: SmsPhoneNumber3,
      SmsPhoneNumberLabel3: SmsPhoneNumber3 ? "Send SMS to" : '',
      MailAddress1: MailAddress1,
      MailAddressLabel1: MailAddress1 ? "Send Email to" : '',
      MailAddress2: MailAddress2,
      MailAddressLabel2: MailAddress2 ? "Send Email to" : '',
      MailAddress3: MailAddress3,
      MailAddressLabel3: MailAddress3 ? "Send Email to" : ''
    })
  }

}

